# Combien y a-t-il de handle dans cette array ?
def getHandleNumber(list) #La fonction getHandleNumber prend une liste en entrée et imprime le nombre de poignées dans la liste en utilisant la propriété length de la liste.
    puts "Nombre de handles => #{list.length}"
    puts "------------------------------------------------\n"
end

# Quel est le handle le plus court de cette liste ?
def getShorterHandle(list)#getShorterHandle(list) est utilisée pour trouver le descripteur le plus court (chaîne) dans une liste de descripteurs. Il prend une liste de chaînes en entrée.
    shorterItem = nil #La fonction déclare deux variables shortItem et shortItemAccount qui sont initialement définies sur nil.
    shorterItemAccount = nil # shortItemAccount qui sont initialement définies sur nil
    list.each do |i| #il parcourt chaque poignée de la liste et vérifie la longueur de chaque poignée.
        lengthI = i.length
        if shorterItem == nil || lengthI < shorterItem #il compare la longueur de la poignée actuelle avec le shortItem et si la poignée actuelle est plus courte que la précédente ou shortItem est nil, il attribue la poignée actuelle et sa longueur à shortItemAccount et shortItem respectivement.
            shorterItem = lengthI # il attribue la poignée actuelle et sa longueur à shortItemAccount et shortItem respectivement.
            shorterItemAccount = i 
        end
    end
    puts "Handle le plus court de la liste => #{shorterItemAccount} (#{shorterItem} caractères)" # il imprime la poignée la plus courte de la liste avec le nombre de caractères qu'elle contient 
    puts "------------------------------------------------\n"
end

# Combien y-a-t'il de handle contenant 5 caractères
# (le @ ne compte pas pour 1 caractère)
def get5charNumberHandle(list)#get5charNumberHandle(list) prend une liste de descripteurs en entrée et compte le nombre de descripteurs de 5 caractères.
    number = 0 #Il commence par initialiser un nombre variable à 0
    list.each do |i|
        if i.slice!(0) == "@" #Ensuite, il parcourt chaque poignée de la liste, il vérifie si la poignée commence par "@" 
            i = i.slice!(1, i.length) #si c'est le cas, il supprime le symbole "@" 
        end
        if i.length == 5 #de la poignée et vérifie si la longueur de la poignée est égal à 5.
            number += 1 #Si la longueur est égale à 5, il incrémente la variable numérique de 1. 
        end
    end
    puts "Nombre de handles de 5 caractères => #{number}" #Après la boucle, il imprime le nombre de poignées de 5 caractères. 
    puts "------------------------------------------------\n"
end

# Combien commencent par une majuscule
# (première lettre juste après le @) ?
def getCapitalizeNumberHandle(list) #getCapitalizeNumberHandle(list) prend une liste de descripteurs en entrée et compte le nombre de descripteurs commençant par une majuscule.
    number = 0 #Il commence par initialiser un numéro de variable à 0.
    list.each do |i|
        if i.slice!(0) == "@" # il parcourt chaque poignée de la liste, il vérifie si la poignée commence par "@"
            i = i.slice!(1, i.length)# il supprime le symbole "@" de la poignée
        end
        if i[0] =~ /[A-Z]/ #vérifie si le premier caractère du handle est une lettre majuscule en utilisant l'expression régulière /[A-Z]/ 
            number += 1 #Si le premier caractère est une lettre majuscule, il incrémente la variable numérique de 1.
        end
    end
    puts "Nombre de handles commençants avec une majuscule => #{number}" #il imprime le nombre de poignées commençant par une lettre majuscule.
    puts "------------------------------------------------\n"
end

# Trie par ordre alphabétique.
def getAlphabeticalSortHandle(list) #getAlphabeticalSortHandle(list) prend une liste de descripteurs en entrée et trie les descripteurs par ordre alphabétique.
    puts "Tri par ordre alphabétique :"
    puts list = list.sort # il utilise la méthode sort sur la liste pour trier les éléments par ordre alphabétique
    puts "------------------------------------------------\n"
end

# Trie croissant par taille des handle
def getSizeSortHandle(list) #getSizeSortHandle(list) prend une liste de descripteurs en entrée et trie les descripteurs par taille dans l'ordre croissant.
    puts "Tri par ordre croissant par taille :"
    puts list = list.sort_by! { |n| n.size } #il utilise le sort_by! méthode sur la liste avec un bloc utilisant { |n| n.size } qui trie les poignées par taille. 
    puts "------------------------------------------------\n"
end

# Quelle est la position dans l'array de la personne @epenser ?
def getValueArrayPositionHandle(list, string) #getValueArrayPositionHandle(list, string) prend une liste de descripteurs et une chaîne en entrée.
    filterList = list.select { |n| n == string } #Il utilise la méthode select pour créer une nouvelle liste appelée filterList qui contient tous les éléments de la liste d'origine qui sont égaux à la chaîne d'entrée
    positionList = list.find_index { |n| n == string } # La fonction utilise ensuite la méthode find_index pour trouver la position de la chaîne dans la liste d'origine. 
    puts "Position de #{filterList} dans l'array : #{positionList}" #Il imprime ensuite la position de la chaîne dans la liste d'origine et une ligne de séparation à la fin.Il convient de noter que la variable filterList ne contiendra toujours qu'un seul élément car la chaîne d'entrée doit être unique dans le tableau, de sorte que la filterList ne contiendra qu'un seul élément.
    puts "------------------------------------------------\n"
end

# Répartition par taille de ces derniers (avec 1, 2, 3... caractères)
def getCharactersSizeSortHandle(list) #getCharactersSizeSortHandle(list) prend une liste de descripteurs en entrée et trie les descripteurs par nombre de caractères dans l'ordre croissant. 
    sortList = list.sort_by! { |n| n.length } #il imprime la liste triée des poignées, il utilise le sort_by! méthode sur la liste avec un bloc utilisant { |n| n.length } qui trie les poignées par le nombre de caractères dans chaque poignée.
    puts "Tri par taille :"
    puts sortList
    puts "------------------------------------------------\n"
end

def perform
    getHandleNumber(handleTwitter)
    getShorterHandle(handleTwitter)
    get5charNumberHandle(handleTwitter)
    getCapitalizeNumberHandle(handleTwitter)
    getAlphabeticalSortHandle(handleTwitter)
    getSizeSortHandle(handleTwitter)
    getValueArrayPositionHandle(handleTwitter, "@epenser")
    getCharactersSizeSortHandle(handleTwitter)
end

perform